import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-discovery',
  templateUrl: './discovery.component.html',
  styleUrls: ['./discovery.component.scss']
})
export class DiscoveryComponent {

  constructor(private http: HttpClient) {}

  public apiName:string='';
public exploreJson:JSON | undefined;

  public discover():void{

  }

  fetchTime(url:string): void {
    this.http.get<any>(url).subscribe(
      (response: any) => {
        this.exploreJson = response;
      }  
      },
      (error: any) => {
        console.error('Fehler beim Abrufen der Zeit:', error);
        this.isApiAvailable = false;
        setTimeout(() => {
          this.fetchTime();
        }, 1000);
      }
    );
  }


}
<h2>Discover</h2>
<p>Discovery by Rest APi of Consul</p>
<p>

<h3>Verwendeter Endpoint</h3> 
<span>http://localhost:8500/v1/catalog/service/</span>
<input type="text" (value)="apiName" />
<button (click)="discover()">discover</button>
